@app.route('/api/picks')
def get_picks():
    """Get picks with filtering"""
    page = request.args.get('page', 1, type=int)
    per_page = request.args.get('per_page', 50, type=int)
    handicapper = request.args.get('handicapper', '')
    sport = request.args.get('sport', '')

    offset = (page - 1) * per_page

    conn = sqlite3.connect(dashboard.db_path)

    try:
        # Build query with filters
        query = "SELECT * FROM picks WHERE 1=1"
        params = []

        if handicapper:
            query += " AND handicapper = ?"
            params.append(handicapper)

        if sport:
            query += " AND sport = ?" 
            params.append(sport)

        query += " ORDER BY created_at DESC LIMIT ? OFFSET ?"
        params.extend([per_page, offset])

        df = pd.read_sql_query(query, conn, params=params)

        # Get total count for pagination
        count_query = "SELECT COUNT(*) FROM picks WHERE 1=1"
        count_params = []

        if handicapper:
            count_query += " AND handicapper = ?"
            count_params.append(handicapper)

        if sport:
            count_query += " AND sport = ?"
            count_params.append(sport)

        cursor = conn.cursor()
        cursor.execute(count_query, count_params)
        total_count = cursor.fetchone()[0]

        return jsonify({
            'picks': df.to_dict('records'),
            'total_count': total_count,
            'page': page,
            'per_page': per_page
        })

    except Exception as e:
        return jsonify({'error': str(e)}), 500
    finally:
        conn.close()

