def enhanced_ocr_processing(image):
    """Enhanced OCR processing with confidence scoring"""
    # Convert to grayscale
    if image.mode != 'L':
        image = image.convert('L')

    # Enhance image
    enhancer = ImageEnhance.Contrast(image)
    image = enhancer.enhance(2.0)

    enhancer = ImageEnhance.Sharpness(image)
    image = enhancer.enhance(2.0)

    # Try multiple OCR configurations
    configs = ['--psm 6', '--psm 4', '--psm 3']
    best_text = ""
    best_confidence = 0

    for config in configs:
        try:
            # Get detailed OCR data
            ocr_data = pytesseract.image_to_data(image, output_type=pytesseract.Output.DICT, config=config)

            # Calculate confidence
            confidences = [int(conf) for conf, text in zip(ocr_data['conf'], ocr_data['text']) 
                         if int(conf) &gt; 0 and text.strip()]

            if confidences:
                avg_confidence = sum(confidences) / len(confidences)
                text = ' '.join([text for text in ocr_data['text'] if text.strip()])

                if avg_confidence &gt; best_confidence:
                    best_confidence = avg_confidence
                    best_text = text
        except:
            continue

    return best_text, best_confidence

def parse_picks_from_text(text):
    """Parse sports picks from OCR text"""
    picks = []

    # Simple parsing logic (you can enhance this)
    nba_teams = {'LAL', 'LAC', 'GSW', 'BOS', 'PHI', 'MIL', 'DEN', 'PHX', 'DAL', 'MIA'}

    # Look for team patterns
    import re
    team_pattern = r'([A-Z]{2,4})\s+(?:VS|@|-)\s+([A-Z]{2,4})'
    matches = re.finditer(team_pattern, text, re.IGNORECASE)

    for match in matches:
        team1, team2 = match.groups()
        team1, team2 = team1.upper(), team2.upper()

        if team1 in nba_teams or team2 in nba_teams:
            picks.append({
                'sport': 'NBA',
                'teams': f"{team1} vs {team2}",
                'pick_type': 'Spread',  # Default
                'pick_value': '+0.0',   # Default
                'confidence': 0.7
            })

    return picks

